import './style.css';
import React from 'react';
import { Document, Page, pdfjs } from 'react-pdf'
pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`

class Resume extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            numPages: null,
            pageNumber: 1
        }
    }

    onDocumentLoadSuccess = ({ numPages }) => {
        this.setState({ numPages })
    }

    handlePageTurn = () => {
        let previousPage = this.state.pageNumber
        this.setState({ pageNumber: 2 })
    }

    render() {
        const { pageNumber, numPages } = this.state
        const pdfPath = '/images/patrickkennedyresumev2.pdf'

        return(
        <div>
            <h1 className="title"><b>Resume</b></h1>
            <h5 className="title"><a style={{textDecoration: 'none'}} href="/images/patrickkennedyresumev2.pdf" download><i className="fas fa-download"></i> Download</a></h5>
            <div>
            <Document
                height={700}
                file={pdfPath}
                onLoadSuccess={this.onDocumentLoadSuccess}
            >
                <Page pageNumber={pageNumber} />
                <div style={{textAlign: 'center'}}>
                <button onClick={this.handlePageTurn} class="waves-effect waves-light btn  blue darken-1">Next Page</button>
                <p style={{textAlign: 'center'}} >Page {pageNumber} of {numPages}</p>
                </div>
            </Document>
            </div>
        </div>
        )
    }
}

export default Resume;
